import React, { useState } from 'react';
import { Grid, Typography, Container, Paper, Tabs, Tab } from '@mui/material';
import Logo from "./logo.svg";

function App() {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  return (
    <Container>
      <Grid container spacing={2}>
        <Grid item xs={12} md={3}>
          <Paper
            style={{
              backgroundColor: '#f5f5f5',
              borderRadius: 8,
              padding: 16,
              margin: 10,
              display: 'flex',
              flexDirection: 'column',
              height: '100%',
            }}
          >
            {/* Logo */}
            <img src="logo.png" alt="Logo" style={{ height: 80, marginBottom: 16 }} />

            {/* Name */}
            <Typography variant="h6" style={{ marginBottom: 16 }}>Your Name</Typography>

            {/* Add your sidebar content here */}
            <Container>
              <Tabs
                value={activeTab}
                onChange={handleTabChange}
                orientation="vertical"
                variant="scrollable"
                scrollButtons="auto"
                indicatorColor="primary"
              >
                <Tab label="About" />
                <Tab label="Work" />
                <Tab label="Experience" />
                <Tab label="Contact" />
              </Tabs>
            </Container>
          </Paper>
        </Grid>
        <Grid item xs={12} md={9}>
          <div style={{ padding: 16 }}>
            {/* Add your main content here */}
            {activeTab === 0 && (
              <Typography variant="body1">Content for Tab 1</Typography>
            )}
            {activeTab === 1 && (
              <Typography variant="body1">Content for Tab 2</Typography>
            )}
            {activeTab === 2 && (
              <Typography variant="body1">Content for Tab 3</Typography>
            )}
          </div>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
