import React from 'react';
import { Typography, Container, Paper, Tabs, Tab, Divider, Grid } from '@mui/material';
import Logo from '../logo.png';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';

function Sidebar({ theme, activeTab, handleTabChange }) {
  return (
    <Paper style={{ borderRadius: 20, padding: 16, margin: 10 }}>
      <Grid container alignItems="center">
        {/* Logo */}
        <Grid item xs={6} sm={12}>
          <Container
            style={{
              height: 120,
              width: 120,
              margin: '16 auto',
              marginBottom: 20,
              borderRadius: 100,
              padding: 24,
              border: 'solid 1px #fff',
            }}
          >
            <img src={Logo} alt='Logo' style={{ objectFit: 'contain', width: '100%', height: '100%' }} />
          </Container>

          {/* Name */}
          <Typography component='span' variant='h5' style={{ marginBottom: 16, textAlign: 'center', display: 'block' }}>
            Rondeo Balos
          </Typography>

          <Divider style={{ marginBottom: 20 }} />
        </Grid>

        {/* Dropdown Button */}
        <Grid item xs={6} sm={12} style={{ textAlign: 'right' }}>
          <ArrowDropDownIcon />
        </Grid>
      </Grid>

      {/* Add your sidebar content here */}
      <Container>
        <Tabs
          value={activeTab}
          onChange={handleTabChange}
          orientation='vertical'
          variant='scrollable'
          scrollButtons='auto'
          indicatorColor='primary'
        >
          <Tab label='About' />
          <Tab label='Work' />
          <Tab label='Experience' />
          <Tab label='Contact' />
        </Tabs>
      </Container>
    </Paper>
  );
}

export default Sidebar;
