import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Grid, Typography, Container, Paper, Tabs, Tab } from '@mui/material';
import Logo from "./logo.png";

function App() {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const darkTheme = createTheme({
    palette: {
      mode: 'dark'
    }
  });

  return (
    <ThemeProvider theme={darkTheme}>
      <Container>
        <Grid container spacing={2}>
          <Grid item xs={12} md={3}>
            <Paper
              style={{
                borderRadius: 8,
                padding: 16,
                margin: 10,
                display: 'flex',
                flexDirection: 'column',
                height: '100%',
              }}
            >
              {/* Logo */}
              <Container
                style={{
                  height: 120, 
                  width: 120, 
                  margin: '16 auto', 
                  borderRadius: 100, 
                  padding: 24,
                  border: 'solid 1px #fff'
                }}
              >
                <img src={Logo} alt="Logo" style={{ objectFit: 'contain', width: '100%', height: '100%' }} />
              </Container>


              {/* Name */}
              <Typography variant="h6" style={{ marginBottom: 16, textAlign: 'center' }}>Rondeo Balos</Typography>

              {/* Add your sidebar content here */}
              <Container>
                <Tabs
                  value={activeTab}
                  onChange={handleTabChange}
                  orientation="vertical"
                  variant="scrollable"
                  scrollButtons="auto"
                  indicatorColor="primary"
                >
                  <Tab label="About" />
                  <Tab label="Work" />
                  <Tab label="Experience" />
                  <Tab label="Contact" />
                </Tabs>
              </Container>
            </Paper>
          </Grid>
          <Grid item xs={12} md={9}>
            <div style={{ padding: 16 }}>
              {/* Add your main content here */}
              {activeTab === 0 && (
                <Typography variant="body1">Content for Tab 1</Typography>
              )}
              {activeTab === 1 && (
                <Typography variant="body1">Content for Tab 2</Typography>
              )}
              {activeTab === 2 && (
                <Typography variant="body1">Content for Tab 3</Typography>
              )}
            </div>
          </Grid>
        </Grid>
      </Container>
    </ThemeProvider>
  );
}

export default App;
