import React from 'react';
import { Typography, Container, Paper, Tabs, Tab, Divider, Grid, IconButton, Avatar } from '@mui/material';
import Logo from '../logo.png';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';

function Sidebar({ theme, activeTab, handleTabChange }) {
  return (
    <Paper style={{ borderRadius: 20, padding: 16, margin: 10 }}>
      <Grid container alignItems="center">
        {/* Logo */}
        <Grid item xs={6} sm={12}>
          <Container
            style={{
              height: 120,
              width: 120,
              margin: '16 auto',
              marginBottom: 20,
              borderRadius: 100,
              padding: 24,
              border: 'solid 1px #fff',
            }}
          >
            <Avatar src={Logo} alt='Logo' />
          </Container>
        </Grid>

        {/* Title */}
        <Grid item xs={6} sm={12} style={{ textAlign: 'center' }}>
          <Typography component='span' variant='h5' style={{ marginBottom: 16 }}>
            Rondeo Balos
          </Typography>
        </Grid>

        {/* Dropdown Button */}
        <Grid item xs={12} sm={12} style={{ textAlign: 'right' }}>
          <IconButton>
            <ArrowDropDownIcon />
          </IconButton>
        </Grid>
      </Grid>

      <Divider style={{ marginBottom: 20 }} />

      {/* Add your sidebar content here */}
      <Container>
        <Tabs
          value={activeTab}
          onChange={handleTabChange}
          orientation='vertical'
          variant='scrollable'
          scrollButtons='auto'
          indicatorColor='primary'
        >
          <Tab label='About' />
          <Tab label='Work' />
          <Tab label='Experience' />
          <Tab label='Contact' />
        </Tabs>
      </Container>
    </Paper>
  );
}

export default Sidebar;
