import React from 'react';
import { Typography, Container, Paper, Tabs, Tab, Divider, Grid, IconButton, useMediaQuery } from '@mui/material';
import Logo from '../logo.png';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';

function Sidebar({ theme, activeTab, handleTabChange }) {
    const isMobile = useMediaQuery( theme.breakpoints.down( 'md' ) );
    return (
        <Paper style={{ borderRadius: 20, padding: 16, margin: 10 }}>
            <Grid container alignItems="center">
                {/* Logo */}
                <Grid item xs={6} sm={12}>
                <Container
                    style={{
                        width: isMobile ? 50 : 120,
                        height: isMobile ? 50 : 120,
                        margin: isMobile ? 0 : '16 auto',
                        marginBottom: isMobile ? 0 : 20,
                        borderRadius: 100,
                        padding: isMobile ? 5 : 24,
                        border: 'solid 1px #fff'
                    }}
                >
                    <img src={Logo} alt='Logo' style={{ objectFit: 'contain', width: '100%', height: '100%' }} />
                </Container>
                </Grid>

                {/* Title */}
                <Grid item xs={6} sm={12} style={{ textAlign: 'center' }}>
                <Typography component='span' variant='h5' style={{ marginBottom: 16 }}>
                    Rondeo Balos
                </Typography>
                </Grid>

                {/* Dropdown Button */}
                <Grid item xs={12} sm={12} style={{ textAlign: 'right' }}>
                <IconButton>
                    <ArrowDropDownIcon />
                </IconButton>
                </Grid>
            </Grid>

            <Divider style={{ marginBottom: 20 }} />

            {/* Add your sidebar content here */}
            <Container>
                <Tabs
                value={activeTab}
                onChange={handleTabChange}
                orientation='vertical'
                variant='scrollable'
                scrollButtons='auto'
                indicatorColor='primary'
                >
                <Tab label='About' />
                <Tab label='Work' />
                <Tab label='Experience' />
                <Tab label='Contact' />
                </Tabs>
            </Container>
        </Paper>
    );
}

export default Sidebar;
